<ol class="breadcrumb"><?php if($info['active']): ?><li><a href="/">Bookmarks</a></li><?php else:?><li><a href="/bookmarks/trash">Trash</a></li><?php endif;?><li class="active"><?php echo $info['title'];?></li></ol><h2> Title: <?php echo $info['title'];?></h2><h2> URL: <a href="<?php echo $info['url'];?>" target="_blank"><?php echo $info['url'];?></a></h2><h5> Created on: <?php echo $info['created'];?></h5><br><?php if($info['active']): ?><a href="/bookmarks/update/<?php echo $info['id']?>" class="btn btn-info">Update</a> <a href="/bookmarks/deactivate/<?php echo $info['id']?>" class="btn btn-danger">Delete</a><?php else:?><a href="/bookmarks/activate/<?php echo $info['id']?>" class="btn btn-success">Move to Bookmarks</a> <a href="/bookmarks/delete/<?php echo $info['id']?>" class="btn btn-danger">Delete For Ever</a><?php endif;?>