<h1>Bookmarks</h1><hr/><?php if(count($bookmarks) < 1): ?><h3>There are no Active Bookmarks at this time.</h3><?php endif;?><?php foreach ($bookmarks as $key=> $value):?><blockquote><p><a href="<?php echo $value['url']?>" target="_blank" title="hols"><?php echo $value['title']?></a></p><small><a href="/bookmarks/detail/<?php echo $value['id']?>" class="btn btn-primary btn-xs">View</a> <a href="/bookmarks/update/<?php echo $value['id']?>" class="btn btn-info btn-xs">Update</a> <a href="/bookmarks/deactivate/<?php echo $value['id']?>" class="btn btn-danger btn-xs">Delete</a></small></blockquote><?php endforeach;?>